contracts/EnterpriseTokenSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./IHederaTokenService.sol";

/**
 * @title EnterpriseTokenSwap
 * @dev Manages swapping Enterprise Tokens for Platform Stablecoins
 * @notice Shops swap their Enterprise Tokens (ENT) for MAD Stablecoins (MAD-S) 1:1
 */
contract EnterpriseTokenSwap is HederaResponseCodes {

    // Hedera Token Service precompiled contract
    IHederaTokenService internal constant HederaTokenService = IHederaTokenService(0x0000000000000000000000000000000000000167);

    // Token addresses
    address public enterpriseToken;     // ENT token
    address public stablecoinToken;     // MAD-S token

    // Minter contract (supplies stablecoins)
    address public minterContract;

    // Admin
    address public admin;

    // Authorized swappers (shops)
    mapping(address => bool) public authorizedSwappers;

    // Swap records
    struct SwapRecord {
        address swapper;
        uint256 enterpriseAmount;
        uint256 stablecoinAmount;
        uint256 timestamp;
        string transferReference;  // Optional reference to original bank transfer
    }

    SwapRecord[] public swapHistory;
    mapping(address => uint256[]) public swapperHistory;

    // Statistics
    uint256 public totalEnterpriseSwapped;
    uint256 public totalStablecoinDistributed;

    // Events
    event TokensSwapped(
        address indexed swapper,
        uint256 enterpriseAmount,
        uint256 stablecoinAmount,
        uint256 timestamp
    );

    event SwapperAuthorized(
        address indexed swapper,
        bool authorized
    );

    event SwapFailed(
        address indexed swapper,
        string reason
    );

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }

    modifier onlyAuthorized() {
        require(authorizedSwappers[msg.sender] || msg.sender == admin, "Not authorized");
        _;
    }

    /**
     * @notice Initialize the swap contract
     * @param _enterpriseToken Enterprise token address (ENT)
     * @param _stablecoinToken Platform stablecoin address (MAD-S)
     * @param _minterContract Minter contract address
     */
    constructor(
        address _enterpriseToken,
        address _stablecoinToken,
        address _minterContract
    ) {
        require(_enterpriseToken != address(0), "Invalid enterprise token");
        require(_stablecoinToken != address(0), "Invalid stablecoin");
        require(_minterContract != address(0), "Invalid minter");

        enterpriseToken = _enterpriseToken;
        stablecoinToken = _stablecoinToken;
        minterContract = _minterContract;
        admin = msg.sender;

        // Admin is automatically authorized
        authorizedSwappers[msg.sender] = true;
    }

    /**
     * @notice Swap Enterprise Tokens for Platform Stablecoins
     * @dev Burns ENT tokens and transfers MAD-S tokens 1:1
     * @param _amount Amount to swap (in cents, 2 decimals)
     */
    function swapEnterpriseForStable(uint256 _amount) 
        external 
        onlyAuthorized 
        returns (bool) 
    {
        return _executeSwap(msg.sender, _amount, "");
    }

    /**
     * @notice Swap with reference (for tracking)
     * @param _amount Amount to swap
     * @param _transferReference Optional reference (e.g., invoice number)
     */
    function swapWithReference(uint256 _amount, string memory _transferReference) 
        external 
        onlyAuthorized 
        returns (bool) 
    {
        return _executeSwap(msg.sender, _amount, _transferReference);
    }

    /**
     * @notice Admin can swap on behalf of authorized swapper
     * @param _swapper Address of the swapper
     * @param _amount Amount to swap
     */
    function swapOnBehalf(address _swapper, uint256 _amount) 
        external 
        onlyAdmin 
        returns (bool) 
    {
        require(authorizedSwappers[_swapper], "Swapper not authorized");
        return _executeSwap(_swapper, _amount, "");
    }

    /**
     * @notice Internal swap execution
     */
    function _executeSwap(
        address _swapper,
        uint256 _amount,
        string memory _transferReference
    ) internal returns (bool) {

        require(_amount > 0, "Amount must be positive");

        // Check stablecoin balance in contract
        require(
            getStablecoinBalance() >= _amount,
            "Insufficient stablecoin liquidity"
        );

        // Step 1: Burn enterprise tokens from swapper
        bool burned = burnEnterpriseTokens(_swapper, _amount);
        if (!burned) {
            emit SwapFailed(_swapper, "Failed to burn enterprise tokens");
            return false;
        }

        // Step 2: Transfer stablecoins to swapper
        bool transferred = transferStablecoins(_swapper, _amount);
        if (!transferred) {
            emit SwapFailed(_swapper, "Failed to transfer stablecoins");
            return false;
        }

        // Record swap
        SwapRecord memory record = SwapRecord({
            swapper: _swapper,
            enterpriseAmount: _amount,
            stablecoinAmount: _amount,  // 1:1 ratio
            timestamp: block.timestamp,
            transferReference: _transferReference
        });

        swapHistory.push(record);
        swapperHistory[_swapper].push(swapHistory.length - 1);

        // Update statistics
        totalEnterpriseSwapped += _amount;
        totalStablecoinDistributed += _amount;

        emit TokensSwapped(_swapper, _amount, _amount, block.timestamp);

        return true;
    }

    /**
     * @notice Burn enterprise tokens from swapper
     */
    function burnEnterpriseTokens(address _from, uint256 _amount) 
        internal 
        returns (bool) 
    {
        // Wipe (burn) enterprise tokens from swapper's account
        int responseCode = HederaTokenService.wipeTokenAccount(
            enterpriseToken,
            _from,
            uint32(_amount)
        );

        return responseCode == HederaResponseCodes.SUCCESS;
    }

    /**
     * @notice Transfer stablecoins to swapper
     */
    function transferStablecoins(address _to, uint256 _amount) 
        internal 
        returns (bool) 
    {
        int responseCode = HederaTokenService.transferToken(
            stablecoinToken,
            address(this),
            _to,
            int64(uint64(_amount))
        );

        return responseCode == HederaResponseCodes.SUCCESS;
    }

    /**
     * @notice Get stablecoin balance of this contract
     * @dev HTS tokens implement ERC20 interface, so we can use balanceOf
     */
    function getStablecoinBalance() public view returns (uint256) {
        // Query the balance using ERC20 balanceOf function
        // HTS tokens automatically implement this interface
        (bool success, bytes memory data) = stablecoinToken.staticcall(
            abi.encodeWithSignature("balanceOf(address)", address(this))
        );

        if (success && data.length >= 32) {
            return abi.decode(data, (uint256));
        }

        return 0; // Return 0 if query fails
    }

    /**
     * @notice Authorize/deauthorize swapper (shop)
     */
    function setAuthorizedSwapper(address _swapper, bool _authorized) 
        external 
        onlyAdmin 
    {
        require(_swapper != address(0), "Invalid address");
        authorizedSwappers[_swapper] = _authorized;
        emit SwapperAuthorized(_swapper, _authorized);
    }

    /**
     * @notice Batch authorize swappers
     */
    function batchAuthorizeSwappers(address[] memory _swappers, bool _authorized) 
        external 
        onlyAdmin 
    {
        for (uint256 i = 0; i < _swappers.length; i++) {
            if (_swappers[i] != address(0)) {
                authorizedSwappers[_swappers[i]] = _authorized;
                emit SwapperAuthorized(_swappers[i], _authorized);
            }
        }
    }

    /**
     * @notice Get swap history for a swapper
     */
    function getSwapperHistory(address _swapper) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return swapperHistory[_swapper];
    }

    /**
     * @notice Get swap record by index
     */
    function getSwapRecord(uint256 _index) 
        external 
        view 
        returns (
            address swapper,
            uint256 enterpriseAmount,
            uint256 stablecoinAmount,
            uint256 timestamp,
            string memory transferReference
        ) 
    {
        require(_index < swapHistory.length, "Index out of bounds");
        SwapRecord memory record = swapHistory[_index];
        return (
            record.swapper,
            record.enterpriseAmount,
            record.stablecoinAmount,
            record.timestamp,
            record.transferReference
        );
    }

    /**
     * @notice Get total number of swaps
     */
    function getTotalSwaps() external view returns (uint256) {
        return swapHistory.length;
    }

    /**
     * @notice Get swap statistics
     */
    function getStatistics() 
        external 
        view 
        returns (
            uint256 totalSwaps,
            uint256 totalEntSwapped,
            uint256 totalMadSDistributed
        ) 
    {
        return (
            swapHistory.length,
            totalEnterpriseSwapped,
            totalStablecoinDistributed
        );
    }

    /**
     * @notice Update admin
     */
    function updateAdmin(address _newAdmin) external onlyAdmin {
        require(_newAdmin != address(0), "Invalid address");
        admin = _newAdmin;
        authorizedSwappers[_newAdmin] = true;
    }

    /**
     * @notice Emergency withdrawal of stablecoins
     */
    function emergencyWithdrawStablecoins(uint256 _amount) 
        external 
        onlyAdmin 
    {
        int responseCode = HederaTokenService.transferToken(
            stablecoinToken,
            address(this),
            admin,
            int64(uint64(_amount))
        );
        require(responseCode == HederaResponseCodes.SUCCESS, "Transfer failed");
    }
}

contracts/IHederaTokenService.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.7;

/**
 * @title HederaResponseCodes
 * @notice Response codes for Hedera Token Service operations
 */
contract HederaResponseCodes {
    int32 internal constant SUCCESS = 22;
    int32 internal constant INVALID_TOKEN_ID = 23;
    int32 internal constant INVALID_ACCOUNT_ID = 24;
    int32 internal constant INSUFFICIENT_TOKEN_BALANCE = 25;
    int32 internal constant TOKEN_NOT_ASSOCIATED_TO_ACCOUNT = 26;
    int32 internal constant INVALID_SIGNATURE = 27;
}

/**
 * @title IHederaTokenService
 * @notice Interface for Hedera Token Service (HTS) system contract
 * @dev Precompiled contract at address 0x167
 */
interface IHederaTokenService {

    /**
     * @notice Mints tokens to the treasury account
     * @param token The token address
     * @param amount Amount to mint (with decimals)
     * @param metadata Optional metadata
     * @return responseCode The response code
     * @return newTotalSupply The new total supply
     * @return serialNumbers Array of serial numbers (for NFTs)
     */
    function mintToken(
        address token,
        uint64 amount,
        bytes[] memory metadata
    ) external returns (int32 responseCode, uint64 newTotalSupply, int64[] memory serialNumbers);

    /**
     * @notice Burns tokens from the treasury account
     * @param token The token address
     * @param amount Amount to burn (with decimals)
     * @param serialNumbers Serial numbers to burn (for NFTs)
     * @return responseCode The response code
     * @return newTotalSupply The new total supply
     */
    function burnToken(
        address token,
        uint64 amount,
        int64[] memory serialNumbers
    ) external returns (int32 responseCode, uint64 newTotalSupply);

    /**
     * @notice Associates a token with an account
     * @param account The account address
     * @param token The token address
     * @return responseCode The response code
     */
    function associateToken(
        address account,
        address token
    ) external returns (int32 responseCode);

    /**
     * @notice Transfers tokens between accounts
     * @param token The token address
     * @param from Sender address
     * @param to Receiver address
     * @param amount Amount to transfer
     * @return responseCode The response code
     */
    function transferToken(
        address token,
        address from,
        address to,
        int64 amount
    ) external returns (int32 responseCode);

    /**
     * @notice Transfers multiple tokens
     * @param tokenTransfers Array of token transfer structs
     * @return responseCode The response code
     */
    function cryptoTransfer(
        TokenTransferList[] memory tokenTransfers
    ) external returns (int32 responseCode);

    /**
     * @notice Grants KYC to an account for a token
     * @param token The token address
     * @param account The account address
     * @return responseCode The response code
     */
    function grantTokenKyc(
        address token,
        address account
    ) external returns (int32 responseCode);

    /**
     * @notice Revokes KYC from an account for a token
     * @param token The token address
     * @param account The account address
     * @return responseCode The response code
     */
    function revokeTokenKyc(
        address token,
        address account
    ) external returns (int32 responseCode);

    /**
     * @notice Freezes an account for a token
     * @param token The token address
     * @param account The account address
     * @return responseCode The response code
     */
    function freezeToken(
        address token,
        address account
    ) external returns (int32 responseCode);

    /**
     * @notice Unfreezes an account for a token
     * @param token The token address
     * @param account The account address
     * @return responseCode The response code
     */
    function unfreezeToken(
        address token,
        address account
    ) external returns (int32 responseCode);

    /**
     * @notice Wipes tokens from an account
     * @param token The token address
     * @param account The account address
     * @param amount Amount to wipe
     * @return responseCode The response code
     */
    function wipeTokenAccount(
        address token,
        address account,
        uint32 amount
    ) external returns (int32 responseCode);

    /**
     * @notice Gets token info
     * @param token The token address
     * @return responseCode The response code
     * @return tokenInfo The token information
     */
    function getTokenInfo(
        address token
    ) external returns (int32 responseCode, TokenInfo memory tokenInfo);

    /**
     * @notice Pauses a token
     * @param token The token address
     * @return responseCode The response code
     */
    function pauseToken(
        address token
    ) external returns (int32 responseCode);

    /**
     * @notice Unpauses a token
     * @param token The token address
     * @return responseCode The response code
     */
    function unpauseToken(
        address token
    ) external returns (int32 responseCode);

    // Structs

    struct TokenTransferList {
        address token;
        AccountAmount[] transfers;
        NftTransfer[] nftTransfers;
    }

    struct AccountAmount {
        address accountID;
        int64 amount;
    }

    struct NftTransfer {
        address senderAccountID;
        address receiverAccountID;
        int64 serialNumber;
    }

    struct TokenInfo {
        HederaToken token;
        uint64 totalSupply;
        bool deleted;
        bool defaultKycStatus;
        bool pauseStatus;
        FixedFee[] fixedFees;
        FractionalFee[] fractionalFees;
        RoyaltyFee[] royaltyFees;
        string ledgerId;
    }

    struct HederaToken {
        string name;
        string symbol;
        address treasury;
        string memo;
        bool tokenSupplyType;
        uint32 maxSupply;
        bool freezeDefault;
        TokenKey[] tokenKeys;
        Expiry expiry;
    }

    struct TokenKey {
        uint256 keyType;
        KeyValue key;
    }

    struct KeyValue {
        bool inheritAccountKey;
        address contractId;
        bytes ed25519;
        bytes ECDSA_secp256k1;
        address delegatableContractId;
    }

    struct Expiry {
        uint32 second;
        address autoRenewAccount;
        uint32 autoRenewPeriod;
    }

    struct FixedFee {
        uint32 amount;
        address tokenId;
        bool useHbarsForPayment;
        bool useCurrentTokenForPayment;
        address feeCollector;
    }

    struct FractionalFee {
        uint32 numerator;
        uint32 denominator;
        uint32 minimumAmount;
        uint32 maximumAmount;
        bool netOfTransfers;
        address feeCollector;
    }

    struct RoyaltyFee {
        uint32 numerator;
        uint32 denominator;
        uint32 amount;
        address tokenId;
        bool useHbarsForPayment;
        address feeCollector;
    }
}

