// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";

/**
 * @title MADBankTransferOracle
 * @dev Chainlink oracle for monitoring Moroccan Dirham (MAD) bank transfers
 * @notice Monitors enterprise bank account transfers to platform account
 */
contract MADBankTransferOracle is ChainlinkClient, ConfirmedOwner {
    using Chainlink for Chainlink.Request;

    // Oracle configuration
    bytes32 private jobId;
    uint256 private fee;

    // MAD Bank Transfer structure
    struct MADBankTransfer {
        string transferReference;   // Unique transfer reference
        string fromAccount;         // Enterprise IBAN
        string toAccount;           // Platform IBAN
        uint256 amount;             // Amount in MAD (2 decimals: 100 = 1.00 MAD)
        uint256 timestamp;          // Transfer timestamp
        string status;              // Transfer status (Booked, Completed)
        bool verified;              // Oracle verification status
        bytes32 requestId;          // Chainlink request ID
        address enterpriseAddress;  // Enterprise Hedera address
    }

    // Mappings
    mapping(bytes32 => MADBankTransfer) public transfers;
    mapping(string => bytes32) public referenceToRequestId;
    mapping(string => bool) public processedReferences;
    mapping(address => bool) public authorizedCallers;

    // Minter contract address
    address public minterContract;

    // Events
    event TransferRequested(
        bytes32 indexed requestId,
        string transferReference,
        string fromAccount,
        address indexed enterpriseAddress
    );

    event TransferVerified(
        bytes32 indexed requestId,
        string transferReference,
        uint256 amount,
        uint256 timestamp,
        string status
    );

    event TransferFailed(
        bytes32 indexed requestId,
        string reason
    );

    event MinterContractUpdated(
        address indexed oldMinter,
        address indexed newMinter
    );

    /**
     * @notice Initialize the oracle contract
     * @param _link LINK token address on Hedera
     * @param _oracle Chainlink oracle address
     * @param _jobId Job ID for MAD bank transfer monitoring
     * @param _fee Fee in LINK tokens
     */
    constructor(
        address _link,
        address _oracle,
        bytes32 _jobId,
        uint256 _fee
    ) ConfirmedOwner(msg.sender) {
        setChainlinkToken(_link);
        setChainlinkOracle(_oracle);
        jobId = _jobId;
        fee = _fee;
        authorizedCallers[msg.sender] = true;
    }

    /**
     * @notice Request verification of MAD bank transfer
     * @param _reference Unique transfer reference (e.g., "EWA-COMPANY-A-202401-001")
     * @param _fromAccount Enterprise bank account IBAN
     * @param _toAccount Platform bank account IBAN
     * @param _enterpriseAddress Enterprise Hedera address
     * @param _apiEndpoint Moroccan bank API endpoint
     * @param _apiKey API authentication key
     * @return requestId Chainlink request ID
     */
    function requestTransferVerification(
        string memory _reference,
        string memory _fromAccount,
        string memory _toAccount,
        address _enterpriseAddress,
        string memory _apiEndpoint,
        string memory _apiKey
    ) public returns (bytes32 requestId) {
        require(authorizedCallers[msg.sender], "Not authorized");
        require(!processedReferences[_reference], "Reference already processed");
        require(bytes(_reference).length > 0, "Invalid reference");

        Chainlink.Request memory req = buildChainlinkRequest(
            jobId,
            address(this),
            this.fulfillTransfer.selector
        );

        // Set API parameters for Moroccan bank
        req.add("get", _apiEndpoint);
        req.add("path", "data,transfer");
        req.add("reference", _reference);
        req.add("fromAccount", _fromAccount);
        req.add("toAccount", _toAccount);
        req.add("apiKey", _apiKey);
        req.add("currency", "MAD");

        // Send request
        requestId = sendChainlinkRequest(req, fee);

        // Store pending transfer
        transfers[requestId] = MADBankTransfer({
            transferReference: _reference,
            fromAccount: _fromAccount,
            toAccount: _toAccount,
            amount: 0,
            timestamp: 0,
            status: "pending",
            verified: false,
            requestId: requestId,
            enterpriseAddress: _enterpriseAddress
        });

        referenceToRequestId[_reference] = requestId;

        emit TransferRequested(requestId, _reference, _fromAccount, _enterpriseAddress);

        return requestId;
    }

    /**
     * @notice Callback function for Chainlink oracle
     * @param _requestId Request ID
     * @param _amount Transfer amount in MAD cents (2 decimals)
     * @param _currency Currency code (should be "MAD")
     * @param _timestamp Transaction timestamp
     * @param _status Transaction status
     */
    function fulfillTransfer(
        bytes32 _requestId,
        uint256 _amount,
        bytes32 _currency,
        uint256 _timestamp,
        bytes32 _status
    ) public recordChainlinkFulfillment(_requestId) {
        MADBankTransfer storage transfer = transfers[_requestId];

        require(bytes(transfer.transferReference).length > 0, "Transfer not found");
        require(!processedReferences[transfer.transferReference], "Already processed");

        // Verify currency is MAD
        string memory currencyStr = bytes32ToString(_currency);
        require(
            keccak256(bytes(currencyStr)) == keccak256(bytes("MAD")),
            "Invalid currency - must be MAD"
        );

        // Update transfer data
        transfer.amount = _amount;
        transfer.timestamp = _timestamp;
        transfer.status = bytes32ToString(_status);
        transfer.verified = true;

        // Mark reference as processed
        processedReferences[transfer.transferReference] = true;

        emit TransferVerified(
            _requestId,
            transfer.transferReference,
            _amount,
            _timestamp,
            transfer.status
        );

        // Notify minter contract if set
        if (minterContract != address(0)) {
            (bool success, ) = minterContract.call(
                abi.encodeWithSignature(
                    "processBankTransfer(string,uint256,address)",
                    transfer.transferReference,
                    transfer.amount,
                    transfer.enterpriseAddress
                )
            );

            if (!success) {
                emit TransferFailed(_requestId, "Minter notification failed");
            }
        }
    }

    /**
     * @notice Get transfer details by request ID
     */
    function getTransfer(bytes32 _requestId) 
        public 
        view 
        returns (
            string memory transferReference,
            string memory fromAccount,
            string memory toAccount,
            uint256 amount,
            uint256 timestamp,
            string memory status,
            bool verified,
            address enterpriseAddress
        ) 
    {
        MADBankTransfer memory transfer = transfers[_requestId];
        return (
            transfer.transferReference,
            transfer.fromAccount,
            transfer.toAccount,
            transfer.amount,
            transfer.timestamp,
            transfer.status,
            transfer.verified,
            transfer.enterpriseAddress
        );
    }

    /**
     * @notice Get transfer by reference
     */
    function getTransferByReference(string memory _reference)
        public
        view
        returns (
            uint256 amount,
            uint256 timestamp,
            string memory status,
            bool verified,
            address enterpriseAddress
        )
    {
        bytes32 requestId = referenceToRequestId[_reference];
        MADBankTransfer memory transfer = transfers[requestId];
        return (
            transfer.amount,
            transfer.timestamp,
            transfer.status,
            transfer.verified,
            transfer.enterpriseAddress
        );
    }

    /**
     * @notice Check if reference has been processed
     */
    function isReferenceProcessed(string memory _reference) 
        public 
        view 
        returns (bool) 
    {
        return processedReferences[_reference];
    }

    /**
     * @notice Set minter contract address
     */
    function setMinterContract(address _minterContract) 
        public 
        onlyOwner 
    {
        require(_minterContract != address(0), "Invalid address");
        address oldMinter = minterContract;
        minterContract = _minterContract;
        emit MinterContractUpdated(oldMinter, _minterContract);
    }

    /**
     * @notice Update oracle configuration
     */
    function updateOracleConfig(
        address _oracle,
        bytes32 _jobId,
        uint256 _fee
    ) public onlyOwner {
        setChainlinkOracle(_oracle);
        jobId = _jobId;
        fee = _fee;
    }

    /**
     * @notice Authorize/deauthorize callers
     */
    function setAuthorizedCaller(address _caller, bool _authorized) 
        public 
        onlyOwner 
    {
        authorizedCallers[_caller] = _authorized;
    }

    /**
     * @notice Withdraw LINK tokens
     */
    function withdrawLink() public onlyOwner {
        LinkTokenInterface link = LinkTokenInterface(chainlinkTokenAddress());
        require(
            link.transfer(msg.sender, link.balanceOf(address(this))),
            "Unable to transfer"
        );
    }

    /**
     * @notice Convert bytes32 to string
     */
    function bytes32ToString(bytes32 _bytes32) 
        internal 
        pure 
        returns (string memory) 
    {
        uint8 i = 0;
        while(i < 32 && _bytes32[i] != 0) {
            i++;
        }
        bytes memory bytesArray = new bytes(i);
        for (i = 0; i < 32 && _bytes32[i] != 0; i++) {
            bytesArray[i] = _bytes32[i];
        }
        return string(bytesArray);
    }
}

contracts/MADBankTransferOracleSimple.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

/**
 * @title MADBankTransferOracleSimple
 * @dev Simplified oracle for testing without Chainlink dependency
 * @notice Monitors enterprise bank account transfers to platform account
 */
contract MADBankTransferOracleSimple {

    // Admin
    address public admin;
    address public minterContract;

    // Authorized callers (can request verification)
    mapping(address => bool) public authorizedCallers;

    // MAD Bank Transfer structure
    struct MADBankTransfer {
        string transferReference;   // Unique transfer reference
        string fromAccount;         // Enterprise IBAN
        string toAccount;           // Platform IBAN
        uint256 amount;             // Amount in MAD (2 decimals: 100 = 1.00 MAD)
        uint256 timestamp;          // Transfer timestamp
        string status;              // Transfer status (Booked, Completed)
        bool verified;              // Oracle verification status
        address enterpriseAddress;  // Enterprise Hedera address
    }

    // Storage
    mapping(string => MADBankTransfer) public transfers;
    mapping(string => bool) public processedReferences;
    string[] public transferReferences;

    // Events
    event TransferVerificationRequested(
        string indexed transferReference,
        string fromAccount,
        string toAccount,
        address indexed enterpriseAddress
    );

    event TransferVerified(
        string indexed transferReference,
        uint256 amount,
        address indexed enterpriseAddress,
        uint256 timestamp
    );

    event MinterContractUpdated(
        address indexed oldMinter,
        address indexed newMinter
    );

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }

    modifier onlyAuthorized() {
        require(authorizedCallers[msg.sender] || msg.sender == admin, "Not authorized");
        _;
    }

    /**
     * @notice Initialize the oracle contract
     * @param _admin Admin address
     */
    constructor(address _admin) {
        admin = _admin;
        authorizedCallers[_admin] = true;
    }

    /**
     * @notice Request verification of MAD bank transfer (simplified)
     * @param _reference Unique transfer reference
     * @param _fromAccount Enterprise bank account IBAN
     * @param _toAccount Platform bank account IBAN
     * @param _amount Transfer amount in cents
     * @param _enterpriseAddress Enterprise Hedera address
     */
    function requestTransferVerification(
        string memory _reference,
        string memory _fromAccount,
        string memory _toAccount,
        uint256 _amount,
        address _enterpriseAddress
    ) external onlyAuthorized returns (bool) {
        require(!processedReferences[_reference], "Reference already processed");
        require(_amount > 0, "Amount must be positive");

        // Store transfer data
        transfers[_reference] = MADBankTransfer({
            transferReference: _reference,
            fromAccount: _fromAccount,
            toAccount: _toAccount,
            amount: _amount,
            timestamp: block.timestamp,
            status: "Pending",
            verified: false,
            enterpriseAddress: _enterpriseAddress
        });

        transferReferences.push(_reference);

        emit TransferVerificationRequested(
            _reference,
            _fromAccount,
            _toAccount,
            _enterpriseAddress
        );

        return true;
    }

    /**
     * @notice Manually verify transfer (for testing)
     * @param _reference Transfer reference
     * @param _status Transfer status
     */
    function verifyTransfer(
        string memory _reference,
        string memory _status
    ) external onlyAdmin returns (bool) {
        require(bytes(transfers[_reference].transferReference).length > 0, "Transfer not found");
        require(!transfers[_reference].verified, "Already verified");

        transfers[_reference].verified = true;
        transfers[_reference].status = _status;
        processedReferences[_reference] = true;

        emit TransferVerified(
            _reference,
            transfers[_reference].amount,
            transfers[_reference].enterpriseAddress,
            block.timestamp
        );

        // Notify minter if configured
        if (minterContract != address(0)) {
            // Call minter to process the transfer
            (bool success, ) = minterContract.call(
                abi.encodeWithSignature(
                    "processBankTransfer(string,uint256,address)",
                    _reference,
                    transfers[_reference].amount,
                    transfers[_reference].enterpriseAddress
                )
            );
            require(success, "Minter notification failed");
        }

        return true;
    }

    /**
     * @notice Get transfer by reference
     */
    function getTransferByReference(string memory _reference) 
        external 
        view 
        returns (
            string memory transferReference,
            string memory fromAccount,
            string memory toAccount,
            uint256 amount,
            uint256 timestamp,
            string memory status,
            bool verified,
            address enterpriseAddress
        ) 
    {
        MADBankTransfer memory transfer = transfers[_reference];
        return (
            transfer.transferReference,
            transfer.fromAccount,
            transfer.toAccount,
            transfer.amount,
            transfer.timestamp,
            transfer.status,
            transfer.verified,
            transfer.enterpriseAddress
        );
    }

    /**
     * @notice Set minter contract address
     */
    function setMinterContract(address _minter) external onlyAdmin {
        address oldMinter = minterContract;
        minterContract = _minter;
        emit MinterContractUpdated(oldMinter, _minter);
    }

    /**
     * @notice Add authorized caller
     */
    function addAuthorizedCaller(address _caller) external onlyAdmin {
        authorizedCallers[_caller] = true;
    }

    /**
     * @notice Remove authorized caller
     */
    function removeAuthorizedCaller(address _caller) external onlyAdmin {
        authorizedCallers[_caller] = false;
    }

    /**
     * @notice Get total transfers
     */
    function getTotalTransfers() external view returns (uint256) {
        return transferReferences.length;
    }

    /**
     * @notice Check if reference is processed
     */
    function isReferenceProcessed(string memory _reference) external view returns (bool) {
        return processedReferences[_reference];
    }
}

contracts/MADStablecoinMinter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./IHederaTokenService.sol";

/**
 * @title MADStablecoinMinter
 * @dev Mints MAD-backed stablecoins based on verified bank transfers
 * @notice Only processes verified transfers from oracle, prevents double-minting
 */
contract MADStablecoinMinter is HederaResponseCodes {

    // Hedera Token Service precompiled contract
    IHederaTokenService internal constant HederaTokenService = IHederaTokenService(0x0000000000000000000000000000000000000167);

    // Oracle contract
    address public oracleContract;

    // Platform stablecoin token
    address public stablecoinToken;

    // Swap contract (receives minted stablecoins)
    address public swapContract;

    // Admin
    address public admin;

    // Processed references (prevent double-minting)
    mapping(string => bool) public processedReferences;

    // Minting records
    struct MintRecord {
        string transferReference;
        uint256 amount;
        address enterpriseAddress;
        uint256 timestamp;
        bool completed;
    }

    mapping(string => MintRecord) public mintRecords;
    string[] public allReferences;

    // Events
    event StablecoinMinted(
        string indexed transferReference,
        uint256 amount,
        address indexed enterpriseAddress,
        uint256 timestamp
    );

    event MintingFailed(
        string indexed transferReference,
        string reason
    );

    event SwapContractUpdated(
        address indexed oldSwap,
        address indexed newSwap
    );

    modifier onlyOracle() {
        require(msg.sender == oracleContract, "Only oracle");
        _;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }

    /**
     * @notice Initialize the minter contract
     * @param _oracleContract Oracle contract address
     * @param _stablecoinToken Platform stablecoin token address
     * @param _swapContract Swap contract address
     */
    constructor(
        address _oracleContract,
        address _stablecoinToken,
        address _swapContract
    ) {
        require(_oracleContract != address(0), "Invalid oracle");
        require(_stablecoinToken != address(0), "Invalid stablecoin");
        require(_swapContract != address(0), "Invalid swap contract");

        oracleContract = _oracleContract;
        stablecoinToken = _stablecoinToken;
        swapContract = _swapContract;
        admin = msg.sender;
    }

    /**
     * @notice Process verified bank transfer and mint stablecoins
     * @dev Called by oracle contract after verification
     * @param _reference Unique transfer reference
     * @param _amount Amount in MAD cents (2 decimals)
     * @param _enterpriseAddress Enterprise Hedera address
     */
    function processBankTransfer(
        string memory _reference,
        uint256 _amount,
        address _enterpriseAddress
    ) external onlyOracle {

        // Prevent double-minting
        require(!processedReferences[_reference], "Reference already processed");
        require(_amount > 0, "Invalid amount");
        require(_enterpriseAddress != address(0), "Invalid enterprise address");

        // Mark as processed
        processedReferences[_reference] = true;

        // Mint stablecoins (1:1 with MAD)
        bool success = mintStablecoins(_amount);

        if (success) {
            // Transfer minted stablecoins to swap contract
            bool transferred = transferToSwapContract(_amount);

            if (transferred) {
                // Record successful mint
                mintRecords[_reference] = MintRecord({
                    transferReference: _reference,
                    amount: _amount,
                    enterpriseAddress: _enterpriseAddress,
                    timestamp: block.timestamp,
                    completed: true
                });

                allReferences.push(_reference);

                emit StablecoinMinted(
                    _reference,
                    _amount,
                    _enterpriseAddress,
                    block.timestamp
                );
            } else {
                emit MintingFailed(_reference, "Transfer to swap contract failed");
            }
        } else {
            emit MintingFailed(_reference, "Minting failed");
        }
    }

    /**
     * @notice Mint platform stablecoins
     * @param _amount Amount to mint (in cents, 2 decimals)
     */
    function mintStablecoins(uint256 _amount) 
        internal 
        returns (bool) 
    {
        // Mint tokens using Hedera Token Service
        (int responseCode, , ) = HederaTokenService.mintToken(
            stablecoinToken,
            uint64(_amount),
            new bytes[](0)
        );

        return responseCode == HederaResponseCodes.SUCCESS;
    }

    /**
     * @notice Transfer minted stablecoins to swap contract
     * @param _amount Amount to transfer
     */
    function transferToSwapContract(uint256 _amount) 
        internal 
        returns (bool) 
    {
        int responseCode = HederaTokenService.transferToken(
            stablecoinToken,
            address(this),
            swapContract,
            int64(uint64(_amount))
        );

        return responseCode == HederaResponseCodes.SUCCESS;
    }

    /**
     * @notice Get mint record by reference
     */
    function getMintRecord(string memory _reference)
        external
        view
        returns (
            uint256 amount,
            address enterpriseAddress,
            uint256 timestamp,
            bool completed
        )
    {
        MintRecord memory record = mintRecords[_reference];
        return (
            record.amount,
            record.enterpriseAddress,
            record.timestamp,
            record.completed
        );
    }

    /**
     * @notice Get total number of processed references
     */
    function getTotalProcessed() external view returns (uint256) {
        return allReferences.length;
    }

    /**
     * @notice Get reference by index
     */
    function getReferenceByIndex(uint256 _index) 
        external 
        view 
        returns (string memory) 
    {
        require(_index < allReferences.length, "Index out of bounds");
        return allReferences[_index];
    }

    /**
     * @notice Update swap contract address
     */
    function setSwapContract(address _newSwapContract) 
        external 
        onlyAdmin 
    {
        require(_newSwapContract != address(0), "Invalid address");
        address oldSwap = swapContract;
        swapContract = _newSwapContract;
        emit SwapContractUpdated(oldSwap, _newSwapContract);
    }

    /**
     * @notice Update admin
     */
    function updateAdmin(address _newAdmin) external onlyAdmin {
        require(_newAdmin != address(0), "Invalid address");
        admin = _newAdmin;
    }

    /**
     * @notice Emergency withdrawal of stablecoins
     */
    function emergencyWithdraw(uint256 _amount) 
        external 
        onlyAdmin 
    {
        int responseCode = HederaTokenService.transferToken(
            stablecoinToken,
            address(this),
            admin,
            int64(uint64(_amount))
        );
        require(responseCode == HederaResponseCodes.SUCCESS, "Transfer failed");
    }
}

